function ajax_upvote_link(){$(".ajax_upvote_link").on("click",function(e){e.stopPropagation();e.preventDefault();var t=$(this);$.ajax($(this).attr("href"),{error:function(e,t,n){alert("Could not upvote, an error occurred. Please try again.")},success:function(e,n,r){var i=$.parseJSON(e);if(i.error){alert(i.error);return}var s="";i["votes"]!=1&&(s="s");t.children(".value").text(i.votes+" vote"+s)}})})}function nav_bar(){$(function(){$("#account_settings_btn").click(function(e){$("#user_modal").modal("show");e.stopPropagation()});$("#navigation_button").click(function(e){$("#navigation_button").toggleClass("active");$("#navigation_items").collapse("toggle")})})};